/***************************/function loadPopup(){if(popupStatus==0){jQuery("#backgroundPopup").css({opacity:"0.7","background-color":"#FAFAFA"});jQuery("#backgroundPopup").fadeIn("slow");jQuery("#panelInner").fadeIn("slow");popupStatus=1}}function disablePopup(){if(popupStatus==1){jQuery("#backgroundPopup").fadeOut("slow");jQuery("#panelInner").fadeOut("slow");popupStatus=0}}function centerPopup(){var e=document.documentElement.clientWidth,t=document.documentElement.clientHeight,n=jQuery("#panelInner").height(),r=jQuery("#panelInner").width();jQuery("#panelInner").css({position:"absolute",top:t/2-n/2,left:e/2-r/2});jQuery("#backgroundPopup").css({height:t})}var popupStatus=0;jQuery(document).ready(function(){jQuery("#open").click(function(){centerPopup();loadPopup()});jQuery(".close").click(function(){disablePopup()});jQuery("#backgroundPopup").click(function(){disablePopup()});jQuery(document).keypress(function(e){e.keyCode==27&&popupStatus==1&&disablePopup()})});